    public static function checkWithSet(array $set, $length = 10000)
    {
        $left = 0;
        foreach ($set as $num => $right) {
            $set[$num] = $left + $right * $length;
            $left = $set[$num];
        }
        $test = mt_rand(1, $length);
        $left = 1;
        foreach ($set as $num => $right) {
            if ($test >= $left && $test <= $right) {
                return $num;
            }
            $left = $right;
        }
